# get client-server time errors for both algorithms
cristian_file = open('cristian.txt')
berkeley_file = open('berkeley.txt')

cristian_errors = []
berkeley_errors = []

for line in cristian_file:
    cristian_errors = list(map(int,line.split(",")[:-1]))

for line in berkeley_file:
    berkeley_errors = list(map(int,line.split(",")[:-1]))

# plot first 20 errors to understand trend
plt.plot(cristian_errors[:20])
plt.plot(berkeley_errors[:20])
plt.title('Difference Between Server Time and Local Clock time')
plt.xlabel('Iteration')
plt.ylabel('Error in ms')
plt.legend(['Cristian', 'Berkeley'])
plt.show()

# get client-global time errors for both algorithms
